// ------------------------------------------------------------------------------------------------------------------------------------------------------
// Layers -----------------------------------------------------------------------------------------------------------------------------------------------
enum layers { 
    _BASE, 
    _LOWER, 
    _RAISE, 
    _ARROWS,
    _ADJUST,
    _FN,

    // US --
    _US_BASE,
    _US_LOWER,
    _US_RAISE,
    _US_ARROWS,
    _US_ADJUST,
    _US_FN,
};

// Current language ?
int is_currently_in_us(void) {

    switch (get_highest_layer(layer_state)) {

        case _US_BASE:
        case _US_LOWER:
        case _US_RAISE:
        case _US_ARROWS:
        case _US_ADJUST:
        case _US_FN:
            return true;

        default:
            return false;
    }
}


